"use strict";
//Equality With Strings
console.log("Equality With Strings");
console.log("apple" == "apple");
console.log("apple" == "APPLE");
//checking with lowercase
console.log("\nchecking with lowercase");
console.log("Pear".toLowerCase() == "Pear");
console.log("Pear".toLowerCase() == "pear");
//Numerical Test 
console.log("\nNumerical Test ");
console.log(4 == 4);
console.log(4 == 5);
console.log(4 > 5);
console.log(4 < 5);
console.log(5 >= 5);
console.log(4 >= 5);
//test using && || operators
console.log("\ntest using && || operators");
console.log(true && false);
console.log(true || false);
//test to check an element in an array
console.log("\ntest to check an element in an array");
let num = [1, 2, 3, 4];
console.log(num.includes(2));
console.log(num.includes(5));
